-- Create model collaborador
CREATE TABLE "C_COLABORADOR" ("LAST_LOGIN" TIMESTAMP NULL, "EMAIL" NVARCHAR2(200) NULL UNIQUE, "PASSWORD" NVARCHAR2(128) NULL, "FOTOP" NVARCHAR2(255) NULL, "RUT" NVARCHAR2(8) NOT NULL PRIMARY KEY, "RUTD" NVARCHAR2(1) NULL, "NOMBRE" NVARCHAR2(200) NULL, "FONO" NVARCHAR2(8) NULL, "DIRECCION" NVARCHAR2(200) NULL, "PAIS" NVARCHAR2(20) NULL, "ESADMIN" NUMBER(1) NOT NULL CHECK ("ESADMIN" IN (0,1)), "IS_STAFF" NUMBER(1) NOT NULL CHECK ("IS_STAFF" IN (0,1)));
